package com.java.programsarthemetic;

public class EqualAndAssign {

	public EqualAndAssign() {
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) {
		int i = 10; //Assigning a value to i;
		int j = 20;
		System.out.println("Value of i after assigning a value to it : "+i);
		if(i == j)
		{
			System.out.println("Both Variable are equal");
			
		}else
		{
			System.out.println("Both Variable are not equal");
		}
	}

}
